    color : 'green'
};


var greet = "";

if (currHour >= 1 && currHour < 12) {
   greet = "Morning";
   cssStyle.color='green';

  }
else if (currHour >= 12 && currHour < 19) { 
  greet = "Afternoon";
  cssStyle.color='orange';
}
else {
   greet = "Night"; 
   cssStyle.color='black';
  }




