import ReactDOM from 'react-dom';


let currDate = new Date(2021,7,5,17);
let currHour = currDate.getHours();

var  greet = "";

if(currHour >=1 && currHour <12)
    greet="Morning";
else if(currHour >=12 && currHour <19)
    greet="Afternoon";
else greet="Night";



ReactDOM.render(
<>
<h1>Hello Sir , Good {greet} </h1>
